import React, { useState } from "react";

const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    retypePassword: "",
  });

  const [formErrors, setFormErrors] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    retypePassword: "",
  });

  const validateForm = () => {
    let errors = {};
    let isValid = true;

    // Validate first name
    if (!formData.firstName.match(/^[a-zA-Z ]+$/)) {
      errors.firstName = "Please enter only letters and spaces";
      isValid = false;
    }

    // Validate last name
    if (!formData.lastName.match(/^[a-zA-Z ]+$/)) {
      errors.lastName = "Please enter only letters and spaces";
      isValid = false;
    }

    // Validate email
    if (!formData.email.match(/^\w+@[a-zA-Z_]+?\.[a-zA-Z]{2,3}$/)) {
      errors.email = "Please enter a valid email address";
      isValid = false;
    }

    // Validate password
    if (formData.password !== formData.retypePassword) {
      errors.retypePassword = "Passwords do not match";
      isValid = false;
    }

    setFormErrors(errors);
    return isValid;
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (validateForm()) {
      // Handle form submission
      console.log("Form submitted");
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="firstName">First Name:</label>
        <input
          type="text"
          id="firstName"
          name="firstName"
          value={formData.firstName}
          onChange={handleChange}
          required
        />
        {formErrors.firstName && <div>{formErrors.firstName}</div>}
      </div>
      <div>
        <label htmlFor="lastName">Last Name:</label>
        <input
          type="text"
          id="lastName"
          name="lastName"
          value={formData.lastName}
          onChange={handleChange}
          required
        />
        {formErrors.lastName && <div>{formErrors.lastName}</div>}
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        {formErrors.email && <div>{formErrors.email}</div>}
      </div>
      <div>
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label htmlFor="retypePassword">Retype Password:</label>
        <input
          type="password"
          id="retypePassword"
          name="retypePassword"
          value={formData.retypePassword}
          onChange={handleChange}
          required
        />
        {formErrors.retypePassword && <div>{formErrors.retypePassword}</div>}
      </div>
      <button type="submit">Register</button>
    </form>
  );
};
