import React, { useEffect, useState } from "react";
import "../css/Profile.css";
import axios from "axios";
import HelperService from "../services/HelperService";

export default function UpdateUser() {
  useEffect(() => {
    const user = HelperService.getCurrentUserData();
    let id = user ? user.username : "";
    let token = user ? user.token : "";
    const url = `http://localhost:8080/user/${id}`;
    const config = {
      headers: { Authorization: `Bearer ${token}` },
    };
    fetchData(url, config);
  }, [fetchData]);
  const [user, setUser] = useState({
    username: "",
    name: "",
    email: "",
  });

  const [formErrors, setFormErrors] = useState({
    username: "",
    name: "",
    email: "",
  });

  const fetchData = async (url, config) => {
    try {
      console.log(url, config);
      const result = await axios.get(url, config);
      console.log(result);

      //set variables
      setUser({
        ...user,
        username: result.data.username,
        name: result.data.name,
        email: result.data.email,
      });
    } catch (error) {
      console.log(error);
    }
  };

  const validateForm = () => {
    let errors = {};
    const nameRegex = /^[a-zA-Z\s]+$/;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const usernameregex = /^[a-zA-Z0-9_-]{4,20}$/;

    // Validate username
    if (!user.username.trim()) {
      errors.username = "Username is required";
    } else if (!usernameregex.test(user.username.trim())) {
      errors.username = "Invalid username";
    }

    // Validate name
    if (!user.name.trim()) {
      errors.name = "Name is required";
    } else if (!nameRegex.test(user.name.trim())) {
      errors.name = "Invalid name";
    }

    // Validate email
    if (!user.email.trim()) {
      errors.email = "Email is required";
    } else if (!emailRegex.test(user.email.trim())) {
      errors.email = "Invalid email";
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setUser({ ...user, [name]: value });
    setFormErrors({ ...formErrors, [name]: "" });
  };

  const [isEditable, setIsEditable] = useState(false);

  const handleEditClick = () => {
    setIsEditable(true);
  };

  const handleSaveClick = (event) => {
    // save changes to user profile
    event.preventDefault();
    if (validateForm()) {
      setIsEditable(false);
      console.log(user);
    }
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="profile-container">
      <h2>Account Settings</h2>
      <form onSubmit={handleSaveClick}>
        <div className="input-container">
          <label htmlFor="username">Username: </label>

          {isEditable ? (
            <input
              className="input"
              type="text"
              id="username"
              name="username"
              value={user.username}
              onChange={handleChange}
            />
          ) : (
            <span className="span">{user.username}</span>
          )}
        </div>
        <p>
          You can use 4 or more characters with a mix of letters, numbers,
          underscores and hyphens{" "}
        </p>
        <div className="error">
          {formErrors.username && <span>{formErrors.username}</span>}
        </div>
        <div className="input-container">
          <label htmlFor="name">Name: </label>
          {isEditable ? (
            <input
              className="input"
              type="text"
              id="name"
              name="name"
              value={user.name}
              onChange={(e) => setUser({ ...user, name: e.target.value })}
            />
          ) : (
            <span className="span">{user.name}</span>
          )}
        </div>
        <div className="error">
          {formErrors.name && <span>{formErrors.name}</span>}
        </div>
        <div className="input-container">
          <label htmlFor="email">Email: </label>
          {isEditable ? (
            <input
              className="input"
              type="email"
              id="email"
              name="email"
              value={user.email}
              onChange={handleChange}
            />
          ) : (
            <span className="span">{user.email}</span>
          )}
        </div>
        <div className="error">
          {formErrors.email && <span>{formErrors.email}</span>}
        </div>
        <div className="button-container">
          {isEditable ? (
            <input type="submit" value="Save" onClick={handleSaveClick} />
          ) : (
            <input type="submit" value="Edit" onClick={handleEditClick} />
          )}
        </div>
      </form>
    </div>
  );
}
