import React, { useState } from "react";
import { useHistory } from "react-router-dom";

export default function RegistrationForm() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [retypePassword, setRetypePassword] = useState("");
  const [errors, setErrors] = useState({});
  const history = useHistory();

  const validateName = (name) => {
    const nameRegex = /^[a-zA-Z ]+$/;
    return nameRegex.test(name);
  };

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validatePassword = (password) => {
    return password.length >= 6;
  };

  const handleNameChange = (e) => {
    setName(e.target.value);
    if (!validateName(e.target.value)) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        name: "Name must contain only letters including spaces",
      }));
    } else {
      setErrors((prevErrors) => ({ ...prevErrors, name: "" }));
    }
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
    if (!validateEmail(e.target.value)) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        email: "Please enter a valid email address",
      }));
    } else {
      setErrors((prevErrors) => ({ ...prevErrors, email: "" }));
    }
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    if (!validatePassword(e.target.value)) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        password: "Password must be at least 6 characters long",
      }));
    } else {
      setErrors((prevErrors) => ({ ...prevErrors, password: "" }));
    }
  };

  const handleRetypePasswordChange = (e) => {
    setRetypePassword(e.target.value);
    if (password !== e.target.value) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        retypePassword: "Passwords do not match",
      }));
    } else {
      setErrors((prevErrors) => ({ ...prevErrors, retypePassword: "" }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Check for errors before submitting the form
    const errorValues = Object.values(errors);
    if (errorValues.length > 0) {
      alert("Please fix the errors before submitting the form");
      return;
    }
    // Submit the form if there are no errors
    console.log("Name:", name);
    console.log("Email:", email);
    console.log("Password:", password);
    console.log("Retype Password:", retypePassword);
    // Clear the form fields
    setName("");
    setEmail("");
    setPassword("");
    setRetypePassword("");
    setErrors({});
    // Navigate to the success page
    history.push("/success");
  };

  return (
    <div>
      <h2>Registration Form</h2>
      <form onSubmit={handleSubmit}></form>
    </div>
  );
}
