import React, { useState } from "react";
import "../css/Profile.css";

export default function Profile(props) {
  const { currentUser } = props;

  const [user, setUser] = useState({
    username: currentUser ? currentUser.username : "",
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const [formErrors, setFormErrors] = useState({
    username: "",
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const validateForm = () => {
    let errors = {};
    const nameRegex = /^[a-zA-Z\s]+$/;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const usernameregex = /^[a-zA-Z0-9_-]{4,20}$/;

    // Validate username
    if (!user.username.trim()) {
      errors.username = "Username is required";
    } else if (!usernameregex.test(user.username.trim())) {
      errors.username = "Invalid username";
    }

    // Validate name
    if (!user.name.trim()) {
      errors.name = "Name is required";
    } else if (!nameRegex.test(user.name.trim())) {
      errors.name = "Invalid name";
    }

    // Validate email
    if (!user.email.trim()) {
      errors.email = "Email is required";
    } else if (!emailRegex.test(user.email.trim())) {
      errors.email = "Invalid email";
    }

    // Validate password
    if (!user.password.trim()) {
      errors.password = "Password is required";
    } else if (user.password.length < 8) {
      errors.password = "Password must be at least 8 characters long";
    }

    // Validate confirm password
    if (!user.confirmPassword.trim()) {
      errors.confirmPassword = "Confirm password is required";
    } else if (user.confirmPassword !== user.password) {
      errors.confirmPassword = "Passwords do not match";
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setUser({ ...user, [name]: value });
    setFormErrors({ ...formErrors, [name]: "" });
  };

  const [isEditable, setIsEditable] = useState(false);

  const handleEditClick = () => {
    setIsEditable(true);
  };

  const handleSaveClick = (event) => {
    // save changes to user profile
    event.preventDefault();
    if (validateForm()) {
      setIsEditable(false);
      console.log(user);
    }
  };

  if (!currentUser) {
    return <div>Loading...</div>;
  }

  return (
    <div className="profile-container">
      <h2>Account Settings</h2>
      <div className="input-container">
        <label htmlFor="username">Username: </label>
        <p>
          You can use 4 or more characters with a mix of letters, numbers,
          underscores and hyphens{" "}
        </p>
        {isEditable ? (
          <input
            className="input"
            type="text"
            id="username"
            name="username"
            value={user.username}
            onChange={handleChange}
          />
        ) : (
          <span className="span">{user.username}</span>
        )}
      </div>
      <div className="error">
        {formErrors.username && <span>{formErrors.username}</span>}
      </div>
      <div className="input-container">
        <label htmlFor="name">Name: </label>
        {isEditable ? (
          <input
            className="input"
            type="text"
            id="name"
            name="name"
            value={user.name}
            onChange={(e) => setUser({ ...user, name: e.target.value })}
          />
        ) : (
          <span className="span">{user.name}</span>
        )}
      </div>
      <div className="error">
        {formErrors.name && <span>{formErrors.name}</span>}
      </div>
      <div className="input-container">
        <label htmlFor="email">Email: </label>
        {isEditable ? (
          <input
            className="input"
            type="email"
            id="email"
            name="email"
            value={user.email}
            onChange={handleChange}
          />
        ) : (
          <span className="span">{user.email}</span>
        )}
      </div>
      <div className="error">
        {formErrors.email && <span>{formErrors.email}</span>}
      </div>
      {isEditable && (
        <>
          <div className="input-container">
            <label htmlFor="password">Password:</label>
            <input
              className="input"
              type="password"
              id="password"
              name="password"
              value={user.password}
              onChange={handleChange}
            />
          </div>
          <div className="error">
            {formErrors.password && <span>{formErrors.password}</span>}
          </div>
          <div className="input-container">
            <label htmlFor="confirmPassword">Confirm Password:</label>
            <input
              className="input"
              type="password"
              id="confirmPassword"
              name="confirmPassword"
              value={user.confirmPassword}
              onChange={handleChange}
            />
          </div>
          <div className="error">
            {formErrors.confirmPassword && (
              <span>{formErrors.confirmPassword}</span>
            )}
          </div>
        </>
      )}
      <div className="button-container">
        {isEditable ? (
          <input type="submit" value="Save" onClick={handleSaveClick} />
        ) : (
          <input type="submit" value="Edit" onClick={handleEditClick} />
        )}
      </div>
    </div>
  );
}
