import React, { useState } from "react";
import "../css/DisplayFlights.css";

export default function DisplayFlights(props) {
  const { flights } = props;

  // State variables to keep track of sorting preference and sorted flights
  const [sortPreference, setSortPreference] = useState("");
  const [sortedFlights, setSortedFlights] = useState(flights);

  // Function to handle sorting based on user preference
  const handleSort = (preference) => {
    let sorted = [];
    if (preference === "price") {
      sorted = flights.sort((a, b) => a.price - b.price);
    } else if (preference === "duration") {
      sorted = flights.sort((a, b) => a.duration - b.duration);
    } else if (preference === "ratings") {
      sorted = flights.sort((a, b) => b.userRatings - a.userRatings);
    }
    setSortPreference(preference);
    setSortedFlights(sorted);
  };

  // If no sort preference is set, display the best and other flights as usual
  if (sortPreference === "") {
    const bestFlights = flights.slice(0, 2);
    const otherFlights = flights.slice(2);

    return (
      <div>
        <div>
          <label htmlFor="sort">Sort by:</label>
          <select id="sort" value={sortPreference} onChange={handleSort}>
            <option value="">Select an option</option>
            <option value="price">Price (Low to High)</option>
            <option value="duration">Duration (Low to High)</option>
            <option value="ratings">Ratings (High to Low)</option>
          </select>
        </div>
        <div>
          <h3 colSpan="7">Best departing flights</h3>
          <table className="table">
            <thead>
              <tr>
                <th>Flight Number</th>
                <th>Departure Time</th>
                <th>Arrival Time</th>
                <th>Duration</th>
                <th>Price</th>
                <th>Ratings</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {bestFlights &&
                bestFlights.map((flight, index) => (
                  <tr key={flight.flightNumber}>
                    <td>{flight.flightNumber} </td>
                    <td>{flight.departureTime}</td>
                    <td>{flight.arrivalTime}</td>
                    <td>{flight.duration} hours</td>
                    <td>{flight.price} </td>
                    <td>{flight.userRatings}</td>
                    <td>Place a button to book</td>
                  </tr>
                ))}
            </tbody>
          </table>
          {otherFlights.length > 0 && (
            <React.Fragment>
              <h3 colSpan="7">Other departing flights</h3>
              <table className="table">
                <thead>
                  <tr>
                    <th>Flight Number</th>
                    <th>Departure Time</th>
                    <th>Arrival Time</th>
                    <th>Duration</th>
                    <th>Price</th>
                    <th>Ratings</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {otherFlights.map((flight, index) => (
                    <React.Fragment key={flight.flightNumber}>
                      <tr>
                        <td>{flight.flightNumber} </td>
                        <td>{flight.departureTime}</td>
                        <td>{flight.arrivalTime}</td>
                        <td>{flight.duration} hours</td>
                        <td>{flight.price} </td>
                        <td>{flight.userRatings}</td>
                        <td>Place a button to book</td>
                      </tr>
                    </React.Fragment>
                  ))}
                </tbody>
              </table>
            </React.Fragment>
          )}
        </div>
      </div>
    );
  } else {
    return <h2>Hey</h2>;
  }
}
