import React, { useState } from "react";
import "../css/Profile.css";

export default function Profile(props) {
  const { currentUser } = props;

  const [user, setUser] = useState({
    username: currentUser ? currentUser.username : "",
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const [formErrors, setFormErrors] = useState({
    username: "",
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const validateForm = () => {
    let errors = {};
    const nameRegex = /^[a-zA-Z\s]+$/;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const usernameregex = /^[a-zA-Z0-9_-]{4,20}$/;

    // Validate username
    if (!user.username.trim()) {
      errors.username = "Username is required";
    } else if (!usernameregex.test(user.username.trim())) {
      errors.username = "Invalid username";
    }

    // Validate name
    if (!user.name.trim()) {
      errors.name = "Name is required";
    } else if (!nameRegex.test(user.name.trim())) {
      errors.name = "Invalid name";
    }

    // Validate email
    if (!user.email.trim()) {
      errors.email = "Email is required";
    } else if (!emailRegex.test(user.email.trim())) {
      errors.email = "Invalid email";
    }

    // Validate password
    if (!user.password.trim()) {
      errors.password = "Password is required";
    } else if (user.password.length < 8) {
      errors.password = "Password must be at least 8 characters long";
    }

    // Validate confirm password
    if (!user.confirmPassword.trim()) {
      errors.confirmPassword = "Confirm password is required";
    } else if (user.confirmPassword !== user.password) {
      errors.confirmPassword = "Passwords do not match";
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const [isEditable, setIsEditable] = useState(false);

  const handleEditClick = () => {
    setIsEditable(true);
  };

  const handleSaveClick = () => {
    // save changes to user profile
    setIsEditable(false);
  };

  if (!currentUser) {
    return <div>Loading...</div>;
  }

  return (
    <div className="profile-container">
      <h2>Account Settings</h2>
      <div className="input-container">
        <label>Username: </label>
        {isEditable ? (
          <input
            className="input"
            type="text"
            value={user.username}
            onChange={(e) => setUser({ ...user, username: e.target.value })}
          />
        ) : (
          <span className="span">{user.username}</span>
        )}
      </div>
      <div className="input-container">
        <label>Name: </label>
        {isEditable ? (
          <input
            className="input"
            type="text"
            value={user.name}
            onChange={(e) => setUser({ ...user, name: e.target.value })}
          />
        ) : (
          <span className="span">{user.name}</span>
        )}
      </div>
      <div className="input-container">
        <label>Email: </label>
        {isEditable ? (
          <input
            className="input"
            type="email"
            value={user.email}
            onChange={(e) => setUser({ ...user, email: e.target.value })}
          />
        ) : (
          <span className="span">{user.email}</span>
        )}
      </div>
      {isEditable && (
        <>
          <div className="input-container">
            <label>Password:</label>
            <input
              className="input"
              type="password"
              value={user.password}
              onChange={(e) => setUser({ ...user, password: e.target.value })}
            />
          </div>
          <div className="input-container">
            <label>Confirm Password:</label>
            <input className="input" type="password" />
          </div>
        </>
      )}
      <div className="button-container">
        {isEditable ? (
          <input type="submit" value="Save" onClick={handleSaveClick} />
        ) : (
          <input type="submit" value="Edit" onClick={handleEditClick} />
        )}
      </div>
    </div>
  );
}
