import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../css/SearchBar.css";
import axios from "axios";
import DisplayFlights from "./DisplayFlights";

const cities = [
  "Toronto",
  "Massachusetts",
  "New York",
  "California",
  "Texas",
  "Florida",
  "Illinois",
  "Georgia",
  "Colorado",
  "Columbia",
];

export default function SearchBar() {
  let navigate = useNavigate();
  const [fromCity, setFromCity] = useState("");
  const [toCity, setToCity] = useState("");
  const [startDate, setStartDate] = useState("");
  const [formErrors, setFormErrors] = useState({
    fromCity: "",
    toCity: "",
    startDate: "",
  });
  const [flights, setFlights] = useState([]);

  const validateForm = () => {
    let errors = {};

    //Validate fromCity
    if (!fromCity.trim()) {
      errors.fromCity = "From City is required";
    }

    //Validate toCity
    if (!toCity.trim()) {
      errors.toCity = "To City is required";
    }

    //Validate startDate
    if (!startDate.trim()) {
      errors.startDate = "Start date is required";
    } else {
      const currentDate = new Date();
      const selectedDate = new Date(startDate);

      if (selectedDate < currentDate) {
        errors.startDate = "Start date should be current or future date";
      }
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleFromCityChange = (event) => {
    const { name, value } = event.target;
    setFromCity(event.target.value);
    setFormErrors({ ...formErrors, [name]: value });
  };

  const handleToCityChange = (event) => {
    const { name, value } = event.target;
    setToCity(event.target.value);
    setFormErrors({ ...formErrors, [name]: value });
  };

  const handleStartDateChange = (event) => {
    const { name, value } = event.target;
    setStartDate(event.target.value);
    setFormErrors({ ...formErrors, [name]: value });
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log(
        `From City: ${fromCity}, To City: ${toCity}, Start Date: ${startDate}`
      );
      const data = {
        origin: fromCity,
        destination: toCity,
        date: startDate,
      };
      axios
        .post("http://localhost:8080/displayFlightsBasedOnInput", data)
        .then((response) => {
          props.setFlights(response.data);
          console.log(response.data); // do something with the response data
        })
        .catch((error) => {
          console.error(error); // handle the error
        });
      navigate("/displayFlights");
    }
  };

  return (
    <div>
      <div className="errors">
        <div>
          {formErrors.fromCity && (
            <span className="error">{formErrors.fromCity}</span>
          )}
        </div>
        <div>
          {formErrors.toCity && (
            <span className="error">{formErrors.toCity}</span>
          )}
        </div>
        <div>
          {formErrors.fromCity && (
            <span className="error">{formErrors.startDate}</span>
          )}
        </div>
      </div>
      <div className="search-bar">
        <form onSubmit={handleSearch}>
          <label htmlFor="origin"></label>
          <select
            className="select"
            value={fromCity}
            onChange={handleFromCityChange}
          >
            <option value="">From City*</option>
            {cities.map((city) => (
              <option key={city} value={city}>
                {city}
              </option>
            ))}
          </select>

          <label htmlFor="destination"></label>
          <select
            className="select"
            value={toCity}
            onChange={handleToCityChange}
          >
            <option value="">To City*</option>
            {cities.map((city) => (
              <option key={city} value={city}>
                {city}
              </option>
            ))}
          </select>

          <label htmlFor="startDate"></label>
          <input
            className="date-picker"
            type="date"
            value={startDate}
            onChange={handleStartDateChange}
          />
          <input
            className="search-btn"
            type="submit"
            value="Search"
            onClick={handleSearch}
          />
        </form>
      </div>
    </div>
  );
}
