import React, { useState } from "react";
import "../css/DisplayFlights.css";

export default function DisplayFlights(props) {
  const { flights } = props;

  const [sortOption, setSortOption] = useState("");

  const sortFlights = (option) => {
    let sortedFlights = [...flights];
    switch (option) {
      case "price":
        sortedFlights.sort((a, b) => a.price - b.price);
        break;
      case "duration":
        sortedFlights.sort((a, b) => a.duration - b.duration);
        break;
      case "ratings":
        sortedFlights.sort((a, b) => b.userRatings - a.userRatings);
        break;
      default:
        break;
    }
    return sortedFlights;
  };

  const sortedFlights = sortOption ? sortFlights(sortOption) : flights;
  const bestFlights = sortedFlights.slice(0, 2);
  const otherFlights = sortedFlights.slice(2);

  return (
    <div>
      <div>
        <div>
          <label htmlFor="sort">Sort By:</label>
          <select
            id="sort"
            name="sort"
            value={sortOption}
            onChange={(e) => setSortOption(e.target.value)}
          >
            <option value="">Select an option</option>
            <option value="price">Price (Low to High)</option>
            <option value="duration">Duration (Low to High)</option>
            <option value="ratings">Ratings (High to Low)</option>
          </select>
        </div>
        <table className="table">
          <thead>
            <tr>
              <th>Flight Number</th>
              <th>Departure Time</th>
              <th>Arrival Time</th>
              <th>Duration</th>
              <th>Price</th>
              <th>Ratings</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {sortedFlights &&
              sortedFlights.map((flight, index) => (
                <tr key={flight.flightNumber}>
                  <td>{flight.flightNumber} </td>
                  <td>{flight.departureTime}</td>
                  <td>{flight.arrivalTime}</td>
                  <td>{flight.duration} hours</td>
                  <td>{flight.price} </td>
                  <td>{flight.userRatings}</td>
                  <td>Place a button to book</td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
